import { Form, Button, Nav } from "react-bootstrap";
import { useState, useEffect, useContext } from "react";
import UserContext from "../UserContext";
import { Navigate } from "react-router-dom";
import Swal from "sweetalert2";

export default function Login(){
  // Allows to consume User context object and it's properties to use for user validation
  const {user, setUser} = useContext(UserContext)
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isActive, setIsActive] = useState(false);

  function loginUser(e) {
    e.preventDefault();

    fetch("http://localhost:4005/b5/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password
      }),
    })
    .then((res) => res.json())
    .then((data) => {

      console.log(data)
      if (data.error === "No Email Found") {

        Swal.fire({
          title: "Email not found.",
          icon: "error",
          text: "Check the email you provided."
        })

      } else if (data.message === "Email and password do not match") {
        Swal.fire({
          title: "Authentication failed",
          icon: "error",
          text: "Check your login details and try again."
        })


      } else if (data.error === "Invalid Email") {
        alert("Invalid Email")
      } else if (typeof data.access !== "undefined"){
        // setUser({access: data.access})
        /* 
          - Set the email of auth user in local storage
            - Syntax: locadStorage.setItem('propertyName', value)
          - The "locadStoarge.setItem" allows to manipulate the browswer's localStorage to store information indefinitely to help demonstrate conditional rendering and the login and logout features.
        */
        // Storing info in locadStorage will make the data persistent event as the page is refreshed unlike with the use of states where the info is reset ehen refreshing the page. 
        localStorage.setItem("token", data.access);
        retrieveUserDetails(data.access);

        Swal.fire({
          title: "Login Successful",
          icon: "success",
          text: "Welcome to Zuitt!"
        })
      } else {
        alert("Something went wrong")
      }
    })
  }

  // Clear input fields
  // setEmail("");
  // setPassword("");

  const retrieveUserDetails = (token) => {
    fetch(`http://localhost:4005/b5/users/login`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(res => res.json())
    .then(data => {
      console.log(data);

      // "setUser" changes the global "user" state to store the "id" and the "isAdmin" property of the user which will be used for validation across the whole application.
      setUser({
        id: data.user._id,
        isAdmin: data.user.isAdmin
      })
    })
  }

  useEffect(() => {
    if (email !== "" &&
    password !== "") {
      setIsActive(true);
    } else {
      setIsActive(false)
    }
  }, [email,password])

  return (
    // (user.id !== null) ?
    // <Navigate to="/courses" />
    // :
    <Form onSubmit={(e) => loginUser(e)}>
      <h1 className="my-5 text-center">Login</h1>
      <Form.Group>
        <Form.Label>Email:</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter Email"
          required
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
      </Form.Group>
      <Form.Group>
        <Form.Label>Password:</Form.Label>
        <Form.Control
          type="password"
          placeholder="Enter Password"
          required
          value={password}
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
      </Form.Group>
      {isActive ? (
        <Button variant="success" type="submit">
          Submit
        </Button>
      ) : (
        <Button variant="disabled" type="submit" disabled>
          Login
        </Button>
      )}
    </Form>

  );

}
