import React, { useState, useEffect, useContext } from "react";
import { Container, Card, Button, Row, Col } from "react-bootstrap";
import { useParams, Link } from "react-router-dom";
import UserContext from "../UserContext";

export default function ProductView() {
  const { productId } = useParams();
  const { user } = useContext(UserContext);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetchProductDetails(productId);
  }, [productId]);

  const fetchProductDetails = (productId) => {
    fetch(`http://localhost:4005/b5/products/${productId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.product && Array.isArray(data.product) && data.product.length === 1) {
          setProduct(data.product[0]); // Assuming the first element is the desired product
        } else {
          console.error("Invalid product data:", data);
        }
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
      });
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={{ span: 6, offset: 3 }}>
          {product && (
            <Card>
              <Card.Body className="text-center">
                <Card.Title>{product.name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{product.description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>PhP {product.price}</Card.Text>
                <Card.Subtitle>Class Schedule</Card.Subtitle>
                <Card.Text>8 am - 5 pm</Card.Text>
                {user.id !== null ? (
                  <Button variant="primary" block disabled>
                    Enroll
                  </Button>
                ) : (
                  <Link className="btn btn-danger btn-block" to="/login">
                    Log in to Enroll
                  </Link>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
}
