import React, { useState, useEffect, useContext } from "react";
import { Container, Card, Button, Row, Col, Form } from "react-bootstrap";
import { useParams, Link } from "react-router-dom";
import UserContext from "../UserContext";

export default function ProductView() {
  const { productId } = useParams();
  const { user } = useContext(UserContext);
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1); // Default quantity is 1
  const [totalPrice, setTotalPrice] = useState(0); // Total price based on quantity

  useEffect(() => {
    fetchProductDetails(productId);
  }, [productId]);

  useEffect(() => {
    if (product) {
      // Calculate total price based on quantity and product price
      setTotalPrice(product.price * quantity);
    }
  }, [quantity, product]);

  const fetchProductDetails = (productId) => {
    fetch(`http://localhost:4005/b5/products/${productId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (
          data.product &&
          Array.isArray(data.product) &&
          data.product.length === 1
        ) {
          setProduct(data.product[0]); // Assuming the first element is the desired product
        } else {
          console.error("Invalid product data:", data);
        }
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
      });
  };

  const handleQuantityChange = (e) => {
    const newQuantity = parseInt(e.target.value);
    if (!isNaN(newQuantity) && newQuantity >= 1) {
      setQuantity(newQuantity);
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={{ span: 6, offset: 3 }}>
          {product && (
            <Card>
              <Card.Body className="text-center">
                <Card.Title>{product.name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{product.description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>PHP {product.price}</Card.Text>
                <Form.Group className="d-flex justify-content-center align-items-center">
                  <Form.Label className="mr-2">Quantity:</Form.Label>
                  <Form.Control
                    type="number"
                    value={quantity}
                    onChange={handleQuantityChange}
                    min={1}
                    style={{ width: "60px" }}
                    className="text-center" // Center the text inside the input field
                  />
                </Form.Group>
                <Card.Subtitle>Total Price:</Card.Subtitle>
                <Card.Text>PHP {totalPrice}</Card.Text>
                {user.id !== null ? (
                  <Button variant="primary">
                    Checkout
                  </Button>
                ) : (
                  <Link className="btn btn-danger btn-block" to="/login">
                    Log in to Checkout
                  </Link>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
}
