import React, { useState, useEffect, useContext } from "react";
import { Container, Card, Button, Row, Col, Form } from "react-bootstrap";
import { useParams, Link, useNavigate } from "react-router-dom";
import UserContext from "../UserContext";

export default function ProductView() {
  const { productId } = useParams();
  const { user } = useContext(UserContext);
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1); // Default quantity is 1
  const navigate = useNavigate(); // Hook to navigate to a different route

  useEffect(() => {
    fetchProductDetails(productId);
  }, [productId]);

  const fetchProductDetails = (productId) => {
    // Fetch product details
  };

  const handleQuantityChange = (e) => {
    // Handle quantity change
  };

  const handleCheckout = () => {
    // Function to handle the checkout process
    const cartItem = {
      productId: product._id, // Assuming product ID is available in the product object
      quantity: quantity,
      // You might need to calculate subtotal based on product price and quantity
    };

    fetch("http://localhost:4005/cart/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({ cartItems: [cartItem] }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Added to cart:", data);
        if (data.error) {
          console.error("Error adding to cart:", data.error);
          // Handle error scenario
        } else {
          // If successfully added to cart, proceed to checkout
          fetch("http://localhost:4005/orders/checkout", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          })
            .then((res) => res.json())
            .then((data) => {
              console.log("Checkout response:", data);
              if (data.error) {
                console.error("Error checking out:", data.error);
                // Handle error scenario
              } else {
                // If checkout is successful, navigate to a success page or display a success message
                navigate("/checkout-success"); // Navigate to a success page
              }
            })
            .catch((error) => {
              console.error("Error checking out:", error);
              // Handle error scenario
            });
        }
      })
      .catch((error) => {
        console.error("Error adding to cart:", error);
        // Handle error scenario
      });
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={{ span: 6, offset: 3 }}>
          {product && (
            <Card>
              <Card.Body className="text-center">
                <Card.Title>{product.name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{product.description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>PHP {product.price}</Card.Text>
                <Form.Group className="d-flex align-items-center justify-content-center">
                  <Form.Label className="mr-2 mb-0">Quantity:</Form.Label>
                  <Form.Control
                    type="number"
                    value={quantity}
                    onChange={handleQuantityChange}
                    min={1}
                    style={{ width: "60px" }}
                    className="text-center"
                  />
                </Form.Group>
                {user.id !== null ? (
                  <Button variant="primary" block onClick={handleCheckout} style={{ marginTop: "20px" }}>
                    Checkout
                  </Button>
                ) : (
                  <Link className="btn btn-danger btn-block" to="/login" style={{ marginTop: "20px" }}>
                    Log in to Checkout
                  </Link>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
}
