import React, { useState, useEffect, useContext } from "react";
import { Container, Card, Button, Row, Col, Form } from "react-bootstrap";
import { useParams, Link, useHistory } from "react-router-dom";
import UserContext from "../UserContext";

export default function ProductView() {
  const { productId } = useParams();
  const { user } = useContext(UserContext);
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1); // Default quantity is 1
  const history = useHistory(); // Hook to navigate programmatically

  useEffect(() => {
    fetchProductDetails(productId);
  }, [productId]);

  const fetchProductDetails = (productId) => {
    fetch(`http://localhost:4005/b5/products/${productId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.product && Array.isArray(data.product) && data.product.length === 1) {
          setProduct(data.product[0]); // Assuming the first element is the desired product
        } else {
          console.error("Invalid product data:", data);
        }
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
      });
  };

  const handleQuantityChange = (e) => {
    const newQuantity = parseInt(e.target.value);
    if (!isNaN(newQuantity) && newQuantity >= 1) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    // Function to add product to cart
    const cartItem = {
      productId: product._id, // Assuming product ID is available in the product object
      quantity: quantity,
      // You might need to calculate subtotal based on product price and quantity
    };

    fetch("http://localhost:4005/cart/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({ cartItems: [cartItem] }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Added to cart:", data);
        if (data.error) {
          console.error("Error adding to cart:", data.error);
          // Handle error scenario
        } else {
          // If successfully added to cart, navigate to the cart page
          history.push("/cart"); // Navigate to the cart page
        }
      })
      .catch((error) => {
        console.error("Error adding to cart:", error);
        // Handle error scenario
      });
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={{ span: 6, offset: 3 }}>
          {product && (
            <Card>
              <Card.Body className="text-center">
                <Card.Title>{product.name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{product.description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>PHP {product.price}</Card.Text>
                <Form.Group className="d-flex align-items-center justify-content-center">
                  <Form.Label className="mr-2 mb-0">Quantity:</Form.Label>
                  <Form.Control
                    type="number"
                    value={quantity}
                    onChange={handleQuantityChange}
                    min={1}
                    style={{ width: "60px" }}
                    className="text-center"
                  />
                </Form.Group>
                {user.id !== null ? (
                  <Button variant="primary" onClick={handleAddToCart}>
                    Add to Cart
                  </Button>
                ) : (
                  <Link className="btn btn-danger btn-block" to="/login">
                    Log in to Add to Cart
                  </Link>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
}
